import React, { useState } from 'react';
import './App.css';

const Game = () => {
  const [points, setPoints] = useState({ A: 1, B: 1 });

  const handleIncrement = () => {
    const randomChar = Math.random() > 0.5 ? 'A' : 'B';
    setPoints(prevPoints => ({
      ...prevPoints,
      [randomChar]: prevPoints[randomChar] + 1
    }));
  };

  const handleReset = () => {
    setPoints({ A: 1, B: 1 });
  };

  const getStatus = () => {
    if (points.A > points.B) return 'A thắng';
    if (points.B > points.A) return 'B thắng';
    return 'Cùng điểm';
  };

  const renderSquares = (count) => {
    return Array.from({ length: count }, (_, index) => (
      <div key={index} className="square"></div>
    ));
  };

  return (
    <div className="container">
      <h1>{getStatus()}</h1>
      <div className="character">
        <h2>A</h2>
        <div className="squares-row">
          {renderSquares(points.A)}
        </div>
      </div>
      <div className="character">
        <h2>B</h2>
        <div className="squares-row">
          {renderSquares(points.B)}
        </div>
      </div>
      <button onClick={handleIncrement}>Tăng điểm</button>
      {(points.A > 1 || points.B > 1) && <button onClick={handleReset}>Đặt lại</button>}
    </div>
  );
};

export default Game;
