import React, { useState } from 'react';

const Game = () => {
  const [points, setPoints] = useState({ A: 1, B: 1 });

  const handleIncrement = () => {
    const randomChar = Math.random() > 0.5 ? 'A' : 'B';
    setPoints(prevPoints => ({
      ...prevPoints,
      [randomChar]: prevPoints[randomChar] + 1
    }));
  };

  const handleReset = () => {
    setPoints({ A: 1, B: 1 });
  };

  const getStatus = () => {
    if (points.A > points.B) return 'A thắng';
    if (points.B > points.A) return 'B thắng';
    return 'Cùng điểm';
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>{getStatus()}</h1>
      <h2>Tên nhân vật: {points.A > points.B ? 'A' : points.B > points.A ? 'B' : 'Cả hai'}</h2>
      <h3>Điểm hiện tại của A: {points.A}</h3>
      <h3>Điểm hiện tại của B: {points.B}</h3>
      <button onClick={handleIncrement}>Tăng điểm</button>
      {(points.A > 1 || points.B > 1) && <button onClick={handleReset}>Đặt lại</button>}
    </div>
  );
};

export default Game;
