import React, { useState } from 'react';
import './App.css';

function App() {
  const [scoreA, setScoreA] = useState(1);
  const [scoreB, setScoreB] = useState(1);
  const [message, setMessage] = useState('');

  const increaseScore = () => {
    const random = Math.random() < 0.5 ? 'A' : 'B';
    if (random === 'A') {
      setScoreA(scoreA + 1);
    } else {
      setScoreB(scoreB + 1);
    }
    updateMessage(scoreA + (random === 'A' ? 1 : 0), scoreB + (random === 'B' ? 1 : 0));
  };

  const updateMessage = (scoreA, scoreB) => {
    if (scoreA > scoreB) {
      setMessage('A thắng');
    } else if (scoreA < scoreB) {
      setMessage('B thắng');
    } else {
      setMessage('Cùng điểm');
    }
  };

  const resetGame = () => {
    setScoreA(1);
    setScoreB(1);
    setMessage('');
  };

  return (
    <div className="App">
      <h1>Tình trạng hiện tại của trò chơi: {message}</h1>
      <h2>Tên nhân vật: A và B</h2>
      <div className="score-bar-container">
        <div className="score-bar score-bar-a" style={{ width: `${scoreA * 20}px` }}></div>
        <div className="score-bar score-bar-b" style={{ width: `${scoreB * 20}px` }}></div>
      </div>
      <button onClick={increaseScore}>Tăng điểm</button>
      {(scoreA > 1 || scoreB > 1) && <button onClick={resetGame}>Đặt lại</button>}
    </div>
  );
}

export default App;
